function findVideos(){for(var a=document.querySelectorAll(".video"),b=0;b<a.length;b++)setupVideo(a[b])}function setupVideo(a){var b=a.querySelector(".video__link"),c=a.querySelector(".video__media"),d=a.querySelector(".video__button"),e=parseMediaURL(c);a.addEventListener("click",function(){var c=createIframe(e);b.remove();d.remove();a.appendChild(c)});b.removeAttribute("href");a.classList.add("video--enabled")}
function parseMediaURL(a){return a.src.match(/https:\/\/i\.ytimg\.com\/vi\/([a-zA-Z0-9_-]+)\/maxresdefault\.jpg/i)[1]}function createIframe(a){var b=document.createElement("iframe");b.setAttribute("allowfullscreen","");b.setAttribute("allow","autoplay");b.setAttribute("src",generateURL(a));b.classList.add("video__media");return b}function generateURL(a){return"https://www.youtube.com/embed/"+a+"?rel=0&showinfo=0&autoplay=1"}findVideos();